import React from 'react'
import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
const Sem_3 = () => {

    const [subject,setsubject]=useState("")
    const[sem,setsem]=useState("Sem-3")
    const [date,setdate]=useState("")
    const [time,settime]=useState("")

 
    
    async function schedule(e){
        e.preventDefault()
 
         const response=await fetch('http://localhost:4000/scheduleclass',{
             method:'POST',
             headers:{
                 'Content-Type':'application/json',
             },
             body:JSON.stringify({
               subject,
               sem,
               date,
               time  
             })
         })
 
         const data = await response.json()
        
     }
    
  return (
    <>
    <form onSubmit={schedule}>
    <div className=" mb-3">
      <label className="form-label">Select Subject</label>
                <select
                  type="text"
                  className="form-control"
                  id="subject"
                  name="subject"
                  value={subject}
                  onChange={(e) => setsubject(e.target.value)}
                >

                  <option value="Software Engineering">Software Engineering</option>
                  <option value="Information System Design">Information System Design</option>
                  <option value="IT Planning and Management">IT Planning and Management</option>
                  <option value="Cloud Computing">Cloud Computing</option>
                </select>
              </div>
    <div className="mb-3">
    <label className="form-label">Semester</label>
                <select
                  type="text"
                  className="form-control"
                  id="sem"
                  name="sem"
                  value={sem}
                >
                  <option>Semester-3</option>
                </select>
              </div>
  <div className="mb-3">
    <label htmlFor="date" className="form-label">Date</label>
    <input type="date" className="form-control" id="date" aria-describedby="date" value={date} onChange={(e)=>setdate(e.target.value)} />
  </div>
  <div className="mb-3">
    <label htmlFor="time" className="form-label">time</label>
    <input type="time" className="form-control" id="time" value={time} onChange={(e)=>settime(e.target.value)}/>
  </div>
  <button type="submit" className="btn btn-primary">Schedule Class</button>
</form>

    </>
  )
}

export default Sem_3;
